import '../App.css'
import './css/Registration.css'

import { useRef, useState, useEffect } from 'react'
import { faCheck, faTimes, faInfoCircle } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import axios from '../api/axios'
import useAuth from '../hooks/useAuth'
import { Link, useNavigate } from 'react-router-dom'
import { resizeEvent, USER_REGEX, PWD_REGEX } from './utils'

const REGISTER_URL = '/auth/registration'

const Registration = () => {

	const userRef = useRef()
	const errRef = useRef()
	const navigate = useNavigate()
	const { setAuth } = useAuth()

	const [Name, setUser] = useState('')
	const [validName, setValidName] = useState(false)
	const [userFocus, setUserFocus] = useState(false)

	const [Password, setPwd] = useState('')
	const [validPwd, setValidPwd] = useState(false)
	const [pwdFocus, setPwdFocus] = useState(false)

	const [matchPwd, setMatchPwd] = useState('')
	const [validMatch, setValidMatch] = useState(false)
	const [matchFocus, setMatchFocus] = useState(false)

	const [errMsg, setErrMsg] = useState('')

	useEffect(() => {setValidName(USER_REGEX.test(Name))}, [Name])
	useEffect(() => {
		resizeEvent()
		userRef.current.focus()
	}, [])

	useEffect(() => {
		setValidPwd(PWD_REGEX.test(Password))
		setValidMatch(Password === matchPwd)
	}, [Password, matchPwd])

	useEffect(() => {setErrMsg('')}, [Name, Password, matchPwd])

	const handleSubmit = async (e) => {
		e.preventDefault()

		const v1 = USER_REGEX.test(Name)
		const v2 = PWD_REGEX.test(Password)
		if (!v1 || !v2) {
			setErrMsg('Invalid Entry')
			return
		}
		try {

			const response = await axios.post(REGISTER_URL, 
				JSON.stringify({ Name, Password }),
				{
                    headers: { 'Content-Type': 'application/json' },
                    withCredentials: true
                }
			)
            const accessToken = response?.data?.accessToken
            setAuth({ accessToken })
            setUser('')
            setPwd('')

            navigate('/CreateGame', { replace: true })

		} catch (err) {
			console.log(err)
			if (!err?.response) {
				setErrMsg('No Server Response')
			} else if (err.response?.status === 409) {
				setErrMsg('Username Taken')
			} else {
				setErrMsg('Registration Failed')
			}
			errRef.current.focus()
		}
	}

	return (
		<>
			<p ref={errRef} className={errMsg ? 'errmsg' : 'offscreen'} aria-live='assertive'>{errMsg}</p>
			<h1>Registration</h1>

			<form onSubmit={handleSubmit}>

				<p className='input-header' htmlFor='Name'>
					Benutzername
					<FontAwesomeIcon icon={faCheck} className={validName ? 'valid' : 'hide'} />
					<FontAwesomeIcon icon={faTimes} className={validName || !Name ? 'hide' : 'invalid'} />
				</p>
				<input
					type='text'
					className='input'
					id='Name'
					placeholder='Benutzername'
					ref={userRef}
					autoComplete='off'
					onChange={(e) => setUser(e.target.value)}
					value={Name}
					required
					aria-invalid={validName ? 'false' : 'true'}
					aria-describedby='uidnote'
					onFocus={() => setUserFocus(true)}
					onBlur={() => setUserFocus(false)}
				/>
				<p id='uidnote' className={userFocus && Name && !validName ? 'instructions' : 'offscreen'}>
					<FontAwesomeIcon icon={faInfoCircle} />
					
				</p>


				<p className='input-header' htmlFor='Password'>
					Passwort
					<FontAwesomeIcon icon={faCheck} className={validPwd ? 'valid' : 'hide'} />
					<FontAwesomeIcon icon={faTimes} className={validPwd || !Password ? 'hide' : 'invalid'} />
				</p>
				<input
					type='password'
					className='input'
					id='Password'
					placeholder='Password'
					onChange={(e) => setPwd(e.target.value)}
					value={Password}
					required
					aria-invalid={validPwd ? 'false' : 'true'}
					aria-describedby='pwdnote'
					onFocus={() => setPwdFocus(true)}
					onBlur={() => setPwdFocus(false)}
				/>
				<p id='pwdnote' className={pwdFocus && !validPwd ? 'instructions' : 'offscreen'}>
					<FontAwesomeIcon icon={faInfoCircle} />
					
				</p>


				<p className='input-header' htmlFor='confirm_pwd'>
					Passwort bestätigen
					<FontAwesomeIcon icon={faCheck} className={validMatch && matchPwd ? 'valid' : 'hide'} />
					<FontAwesomeIcon icon={faTimes} className={validMatch || !matchPwd ? 'hide' : 'invalid'} />
				</p>
				<input
					type='password'
					className='input'
					id='confirm_pwd'
					placeholder='Password'
					onChange={(e) => setMatchPwd(e.target.value)}
					value={matchPwd}
					required
					aria-invalid={validMatch ? 'false' : 'true'}
					aria-describedby='confirmnote'
					onFocus={() => setMatchFocus(true)}
					onBlur={() => setMatchFocus(false)}
				/>
				<p id='confirmnote' className={matchFocus && !validMatch ? 'instructions' : 'offscreen'}>
					<FontAwesomeIcon icon={faInfoCircle} />
					Die Passwörter müssen gleich sein.
				</p>
				<br/>
				<br/>

				<button className='button' style={{ width: '100%'}} disabled={!validName || !validPwd || !validMatch ? true : false}>Registrieren</button>

			</form>
			
			<p className='reglog-link-switch'>
				Bereits registriert?{' '}
				<span className='line'>
					<Link to='/'>Einloggen</Link>
				</span>
			</p>
		</>

	)

}

export default Registration

























