import '../App.css'

import React, { useEffect, useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { clearSessionStorage, sessionStorage_session, sessionStorage_finalscores, formatDate } from './utils'
import useAxiosPrivate from '../hooks/useAxiosPrivate'


function SessionPreview() {
	
	const navigate = useNavigate()
	const axiosPrivate = useAxiosPrivate()
	const session = JSON.parse(sessionStorage.getItem(sessionStorage_session))

	const [list] = useState([])
	const [finalScores, setFinalScores] = useState([])
	const [loaderVisible, setLoaderVisible] = useState(false)
	const [showLastFinalScores, setShowLastFinalScores] = useState(0)

	const firstColumnWidth = '150px'
	const firstColumnStyle = { width: firstColumnWidth, minWidth: firstColumnWidth, maxWidth: firstColumnWidth, padding: '5px', fontWeight: 'bold' }
	const columnWidth = session?.List_Players?.length > 8 ? '75px' : (session?.List_Players?.length > 4 ? '125px' : '200px')
	const style = { width: columnWidth, minWidth: columnWidth, maxWidth: columnWidth, padding: '5px', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }

	

	

	useEffect(() => {
		
		if(!session || !session.List_Players) return navigate('/creategame', { replace: true })

		async function request() {

			setLoaderVisible(true)

			await axiosPrivate.get('/sessionpreview',
				{
					headers: { 'Content-Type': 'application/json' },
					params: { id: session.id },
					withCredentials: true
				}
			).then((res) => {
				setFinalScores(JSON.parse(res.data))
				setFinalScores([
					{Played: '2022-7-30T10:10:51.710Z', Columns: '1', Surrender: false, Player_0: '0', Player_1: '10'},
					{Played: '2022-8-30T10:10:51.710Z', Columns: '1', Surrender: false, Player_0: '1', Player_1: '11'},
					{Played: '2022-9-30T10:10:51.710Z', Columns: '1', Surrender: false, Player_0: '2', Player_1: '12'},
					{Played: '2022-10-30T10:10:51.710Z', Columns: '1', Surrender: false, Player_0: '3', Player_1: '13'},
					{Played: '2023-1-30T10:10:51.710Z', Columns: '1', Surrender: false, Player_0: '4', Player_1: '14'},
					{Played: '2023-3-30T10:10:51.710Z', Columns: '1', Surrender: false, Player_0: '5', Player_1: '15'},
					{Played: '2023-5-30T10:10:51.710Z', Columns: '1', Surrender: false, Player_0: '6', Player_1: '16'},
					{Played: '2023-7-30T10:10:51.710Z', Columns: '1', Surrender: false, Player_0: '7', Player_1: '17'},
					{Played: '2023-8-30T10:10:51.710Z', Columns: '1', Surrender: false, Player_0: '8', Player_1: '18'},
					{Played: '2023-10-30T10:10:51.710Z', Columns: '1', Surrender: false, Player_0: '9', Player_1: '19'},
				])
				console.log(JSON.parse(res.data))
			}).catch((err) => {
				const status = err.response.status
				if(status === 404) {
					window.alert('Die Partie wurde nicht gefunden!')
					navigate('/selectsession', { replace: true })
				} else {
					window.alert('Unknown error:', err)
				}
			})

			setLoaderVisible(false)

		}

		request()

	}, [])
	




	const back = () => {
	
		clearSessionStorage()
		navigate('/selectsession', { replace: true })
	
	}





	const showAnalytics = () => {

		sessionStorage.setItem(sessionStorage_finalscores, JSON.stringify(finalScores))

	}

	const handleShowLastFinalScores = (e) => {
		setShowLastFinalScores(e.target.value)
		list.length = 0
		if(e.target.value === '0') {
			console.log('Succ')
		} else {

		}
	}

	// const 





	return (
		<>

			<table className='table'>
				<tbody>
					<tr>
						<td style={firstColumnStyle}>Spieler</td>
						{session?.List_Players?.map((p, i) => (
							<td key={i} style={style}>{p.Name}</td>
						))}
					</tr>
					<tr>
						<td style={{ width: firstColumnWidth, minWidth: firstColumnWidth, maxWidth: firstColumnWidth, padding: '5px', fontWeight: 'bold' }}>
							Gewonnen<br/>
							<select 
								value={showLastFinalScores}
								onChange={handleShowLastFinalScores}
								style={{ 
									color: 'var(--text-color)', 
									border: 'none', 
									outline: 'none' 
								}}>
								<option value={0}>Dieses Jahr</option>
								<option value={1}>Dieser Monat</option>
							</select>
						</td>
						{session?.List_Players?.map((p, i) => (
							<td key={i} style={style}>{p.Wins}</td>
						))}
					</tr>
				</tbody>
			</table>

			<table className='table'>
				<tbody>
					{list?.map((fs, i) => (
						<tr key={i}>
							<td style={firstColumnStyle}>{formatDate(fs.Played)}</td>
							{session?.List_Players?.map((p, j) => (
								<td key={`${i}.${j}`} style={style}>{fs[p.Alias]}</td>
							))}
						</tr>
					))}
				</tbody>
			</table>
			
			<div className={`loader ${loaderVisible ? '' : 'notVisible'}`}>
				<span/>
				<span/>
				<span/>
			</div>
				
			<button className='button' style={{ height: '40px', width: '100%', marginBottom: '0px' }} onClick={() => navigate('/game', { replace: true })}>Los geht's!</button>

			<div style={{ display: 'flex', justifyContent: 'space-between' }}>
				<p className='link-switch'>
					<Link to='/selectsession' onClick={back}>Zur√ºck</Link>
				</p>
				<p className='link-switch'>
					<Link to='/sessionanalytics' onClick={showAnalytics}>Mehr {'>'}</Link>
				</p>
			</div>

		</>
	)
}

export default SessionPreview
