import '../App.css'
import './css/SelectSession.css'

import React, { useEffect, useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { resizeEvent } from './utils'
import useAxiosPrivate from '../hooks/useAxiosPrivate'


function SelectSession() {


	const axiosPrivate = useAxiosPrivate()

	// 	const target = event.target
	// 	if(target.tagName !== 'BUTTON') {
			
	// 		const clickedElement = target.closest('dt')
	// 		const index = clickedElement.getAttribute('gameSessionIndex')
	// 		sessionStorage.setItem(sessionStorage_players, JSON.stringify(players_list[index]))
	// 		sessionStorage.setItem(sessionStorage_gameAttributes, JSON.stringify(gameAttributes_list[index]))

	// 		window.location.replace('/sessionpreview')

	// 	} else {
			
	// 		if (window.confirm('Bist du sicher, dass du diese Session löschen möchtest?')) {
				
	// 			const sessionName = target.getAttribute('gameSessionName')
				
	// 			await fetch('/deletesession', {
	// 				method: 'POST',
	// 				headers: { 'Content-Type': 'application/json' },
	// 				body: JSON.stringify({ SessionName: sessionName })
	// 			})
		
	// 			window.location.reload()

	// 		}

	// 	}


	const [list, setList] = useState([])
	const message = 'Es gibt noch keine Partie!'
	const list_checkbox = list.map(() => false)
	const [ enabled, setEnabled] = useState(false)

	useEffect(() => {

		axiosPrivate.get('/selectsession').then((res) => {
			const l = res.data
			const tmp = []

			for(const e of l) {
				tmp.push({ 
					Attributes: JSON.parse(e.Attributes), 
					List_Players: JSON.parse(e.List_Players) 
				})
			}
			
			setList(tmp)
		})

		resizeEvent()

	}, [])


	const handleDelete = (e) => {
		console.log('handle delete')
	}

	const test = (e) => {
		console.log(e.target)
	}

	const handleCheckbox = (index, checked) => {
		list_checkbox[index] = checked
		if(checked) {
			setEnabled(true)
		} else {
			for(const e of list_checkbox) {
				if(e) return
			}
			setEnabled(false)
		}

	}


	return (
		<>
			<button aria-disabled={enabled}>Delete</button>
			<dl id='sessionList'>
				{list.length === 0 ? (
					<dt className='message'>{message}</dt>
				) : (
					list.map((s, i) => (
						<dt className='listElement' index={i} key={i}>
							<input className='checkbox-delete' type='checkbox' onChange={(e) => handleCheckbox(i, e.target.checked)} />
							<label className='label' onClick={test}>
								{s.List_Players.map((p) => p.Name).join(' vs ')}
							</label>
							<div className='container'>
								<label className='label'>{s.Attributes.CreatedDate}</label>
								<button className='deleteGameButton' onClick={handleDelete}>X</button>
							</div>
						</dt>
					))
				)}
			</dl>
			
			<p className='loadGames'>
				<Link to='/creategame'>Erstelle Spiel</Link>
			</p>
		</>
	)
}

export default SelectSession
