	const bottomTable_rows = [
		{ td:
			<>
				<td style={{ borderLeft: thickBorder, borderTop: thickBorder }}><label>Dreiferpasch</label></td>
				<td style={{ borderTop: thickBorder }}><label>alle Augen<br/>zählen</label></td>
