		const e = element.target
		if (isNaN(parseFloat(e.value)) || !isFinite(e.value) || e.value.length > 2) {
			e.value = ''
			return
		}

		const id = element.target.getAttribute('tableid')
		const column = Number(element.target.getAttribute('column'))
		
